import { useState, useEffect } from "react";

const Darkmode = () => {
  // üåô Îã§ÌÅ¨ Î™®Îìú ÏÉÅÌÉú(state) Í¥ÄÎ¶¨
  const [isDarkMode, setIsDarkMode] = useState(() => {
    // Î°úÏª¨ Ïä§ÌÜ†Î¶¨ÏßÄÏóêÏÑú Ï†ÄÏû•Îêú Îã§ÌÅ¨ Î™®Îìú Í∞í Î∂àÎü¨Ïò§Í∏∞
    return localStorage.getItem("darkMode") === "enabled";
  });

  // üåô Îã§ÌÅ¨ Î™®Îìú ÌÜ†Í∏Ä Ìï®Ïàò
  const toggleDarkMode = () => {
    setIsDarkMode((prev) => !prev);
  };

  // üåô Îã§ÌÅ¨ Î™®Îìú ÏÉÅÌÉúÍ∞Ä Î≥ÄÍ≤ΩÎê† ÎïåÎßàÎã§ Ï†ÅÏö©
  useEffect(() => {
    if (isDarkMode) {
      document.documentElement.classList.add("dark-mode");
      localStorage.setItem("darkMode", "enabled");
    } else {
      document.documentElement.classList.remove("dark-mode");
      localStorage.setItem("darkMode", "disabled");
    }
  }, [isDarkMode]); // isDarkModeÍ∞Ä Î∞îÎÄî ÎïåÎßàÎã§ Ïã§Ìñâ

  return (
    <button onClick={toggleDarkMode} className="dark-mode-toggle">
      {isDarkMode ? "üåû ÎùºÏù¥Ìä∏ Î™®Îìú" : "üåô Îã§ÌÅ¨ Î™®Îìú"}
    </button>
  );
};

export default Darkmode;
