// src/components/Sidebar.jsx
import { useAuth } from '../../context/AuthContext';
import sidebarIcon from '../assets/images/menu_24dp.svg'; // 경로 예시

const Sidebar = () => {
  const { user, isAuthenticated, isAdmin } = useAuth();

  return (
    <div className="sidebar">
      <div className="menu">
        <img className="menu-icon" src={sidebarIcon} alt="Menu" />
      </div>
      <hr className="divider" />

      {!isAuthenticated && (
        <div className="guest-view">
          <div className="profile">
            <img src="/images/account_circle.svg" alt="User" className="profile-image" />
            <button className="primary-button loginPage">로그인</button>
          </div>
        </div>
      )}

      {isAuthenticated && (
        <div className={`${isAdmin ? 'admin-view' : 'user-view'}`}>
          <div className="profile">
            <img
              src={`https://soundbrew.storage.s3.ap-northeast-2.amazonaws.com/${user.profileImagePath}`}
              alt="프로필"
              className="profile-image"
            />
          </div>
          <div className="info1">
            <span className="myNickname">{user.nickname}</span>
            <span className="plans">{isAdmin ? '관리자' : '회원'}</span>
          </div>
        </div>
      )}

      {/* 이하 기타 메뉴들 */}
    </div>
  );
};

export default Sidebar;
