// src/App.js
import React, { useEffect } from "react";
import { BrowserRouter as Router, useNavigate } from "react-router-dom";
import AppRouter from "./routes/AppRouter";
import { setGlobalNavigate } from "./utils/response/globalNavigate";
import { GlobalStateProvider } from "./state/State";

// Router 내부에서 useNavigate를 사용할 수 있는 컴포넌트
function AppWithNavigationSetter() {
  const navigate = useNavigate();

  useEffect(() => {
    setGlobalNavigate(navigate);
  }, [navigate]);

  // AppRouter는 이미 내부에서 라우트 설정(<Routes> 등)을 담당하므로,
  // AppWithNavigationSetter는 단순히 네비게이션을 설정한 후 AppRouter를 반환합니다.
  return <AppRouter />;
}

function App() {
  return (
    // 최상위에 <Router>를 배치합니다.
    <Router>
      <GlobalStateProvider>
        <AppWithNavigationSetter />
      </GlobalStateProvider>
    </Router>
  );
}

export default App;
