// src/components/DarkModeToggle.jsx
import React from "react";
import { useDarkMode } from "../hooks/useDarkMode";
// 아이콘 경로는 실제 프로젝트 구조에 맞게 수정하세요.
import lightModeIcon from "../../assets/images/light_mode_24dp.svg";
import darkModeIcon from "../../assets/images/dark_mode_24dp.svg";

const DarkModeToggle = () => {
  const [darkMode, toggleDarkMode] = useDarkMode();

  return (
    <div className="toggle-container">
      <img src={lightModeIcon} alt="Light Mode" />
      <input
        type="checkbox"
        id="darkModeToggle"
        className="darkMode-checkbox"
        checked={darkMode}
        onChange={toggleDarkMode}
      />
      <label htmlFor="darkModeToggle" className="darkMode-label"></label>
      <img src={darkModeIcon} alt="Dark Mode" />
    </div>
  );
};

export default DarkModeToggle;
