// src/pages/Login.js
import { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../../css/login.css"; // 로그인 전용 CSS

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("https://localhost:8443/generateToken", { username, password });
      localStorage.setItem("accessToken", response.data.accessToken);
      localStorage.setItem("refreshToken", response.data.refreshToken);
      // 로그인 성공 후 SPA 내에서 리다이렉션
      navigate(response.data.redirectUrl);
    } catch (error) {
      localStorage.removeItem("accessToken");
      localStorage.removeItem("refreshToken");
      alert(error.response.data.message);
      // 실패 시, 필요한 경우 별도의 처리 로직
      if (error.response.data.resetToken) {
        localStorage.setItem("resetToken", error.response.data.resetToken);
      }
      window.location.href = error.response.data.redirectUrl;
    }
  };

  return (
    <div className="content-wrapper">
      <div className="content-header">
        로그인
      </div>

      <div className="content-body">
        <form id="login-form" className="login-form" action="/login" method="post">
          <div className="input-group">
            <label htmlFor="email">이메일</label>
            <input type="text"id="email"name="username"placeholder="이메일 입력"required
            />
          </div>

          <div className="input-group">
            <label htmlFor="password">비밀번호</label>
            <input type="password" id="password" name="password" placeholder="비밀번호 입력" required/>
          </div>

          <button type="submit">로그인</button>
        </form>
      </div>

      <div className="content-footer">
        <span>
          <a href="/help/find-password">비밀번호 찾기</a>
        </span>
      </div>
    </div>
  );
};

export default Login;
