// src/components/layout/Sidebar.js
import { useState, useRef, useEffect } from 'react';
import { useAuth } from '../../context/authContext';
import sidebarIcon from '../../assets/images/menu_24dp_5F6368_FILL0_wght400_GRAD0_opsz24.svg';
import closeIcon from '../../assets/images/close_24dp_5F6368_FILL0_wght400_GRAD0_opsz24.svg';
import soundIcon from '../../assets/images/music_note_24dp_5F6368_FILL0_wght400_GRAD0_opsz24.svg';
import subscriptionIcon from '../../assets/images/graphic_eq_24dp_5F6368_FILL0_wght400_GRAD0_opsz24.svg';
import qnaIcon from '../../assets/images/help_24dp_5F6368_FILL0_wght400_GRAD0_opsz24.svg';
import lightModeIcon from '../../assets/images/light_mode_24dp_5F6368_FILL0_wght400_GRAD0_opsz24.svg';
import darkModeIcon from '../../assets/images/dark_mode_24dp_5F6368_FILL0_wght400_GRAD0_opsz24.svg';
import "../../assets/css/layout.css";
import "../../assets/css/common.css";

const Sidebar = () => {
  const { user, isAuthenticated, isAdmin } = useAuth();

  // 사이드바 확장 여부 state
  const [isExpanded, setIsExpanded] = useState(false);
  // 내부 메뉴 열린 상태 관리
  const [userInfoOpen, setUserInfoOpen] = useState(false);
  const [adminInfoOpen, setAdminInfoOpen] = useState(false);

  // useRef를 통해 DOM 요소 참조 생성
  const sidebarRef = useRef(null);
  const menuButtonRef = useRef(null);

  // 사이드바 토글 핸들러
  const handleSidebarToggle = () => {
    setIsExpanded((prev) => {
      // 만약 닫히는 상태라면 내부 메뉴도 닫기
      if (prev) {
        setUserInfoOpen(false);
        setAdminInfoOpen(false);
      }
      return !prev;
    });
  };

  // 외부 클릭 감지를 위한 핸들러
  const handleDocumentClick = (e) => {
    if (
      isExpanded &&
      sidebarRef.current &&
      menuButtonRef.current &&
      !sidebarRef.current.contains(e.target) &&
      !menuButtonRef.current.contains(e.target)
    ) {
      setIsExpanded(false);
      setUserInfoOpen(false);
      setAdminInfoOpen(false);
    }
  };

  // 컴포넌트 마운트 시 문서 클릭 이벤트 추가 및 클린업
  useEffect(() => {
    document.addEventListener('click', handleDocumentClick);
    return () => document.removeEventListener('click', handleDocumentClick);
  }, [isExpanded]);

  return (
    <div ref={sidebarRef} className={`sidebar ${isExpanded ? 'expanded' : ''}`}>
      <div ref={menuButtonRef} className="menu" onClick={handleSidebarToggle}>
        <img
          className="menu-icon"
          src={isExpanded ? closeIcon : sidebarIcon}
          alt="Menu Toggle"
        />
      </div>
      <hr className="divider" />

      {!isAuthenticated && (
        <div className="guest-view">
          <div className="profile">
            <img src="/images/account_circle.svg" alt="User" className="profile-image" />
            <button className="primary-button loginPage">로그인</button>
          </div>
        </div>
      )}

      {isAuthenticated && (
        <>
          {isAdmin ? (
            <div className="admin-view">
              <div className="profile">
                <img
                  onError={(e) => { e.target.onerror = null; e.target.src = '/images/account_circle.svg'; }}
                  alt="User Profile"
                  className="profile-image"
                />
              </div>
              <div className="info1">
                <span className="myNickname">{user.nickname}</span>
                <span className="plans">관리자</span>
              </div>
              <div className="sidebar-admin-info">
                <div className="sidebar-admin-info-title" onClick={() => setAdminInfoOpen(prev => !prev)}>
                  <div className="sidebar-item">
                    <img className="sidebar-icon" src="/images/info_24dp_5F6368_FILL0_wght400_GRAD0_opsz24.svg" alt="Info" />
                    <span className="sidebar-text">더 보기</span>
                  </div>
                </div>
                {adminInfoOpen && (
                  <div className="admin-view-content">
                    <div className="sidebar-item">
                      <img className="sidebar-icon" src="/images/psychology_alt_24dp_5F6368_FILL0_wght400_GRAD0_opsz24.svg" alt="Statistics" />
                      <span className="sidebar-text">사이트 통계 조회</span>
                    </div>
                    <div className="sidebar-item">
                      <img className="sidebar-icon" src="/images/psychology_alt_24dp_5F6368_FILL0_wght400_GRAD0_opsz24.svg" alt="User Info" />
                      <span className="sidebar-text">회원 정보 조회</span>
                    </div>
                    <div className="sidebar-item">
                      <img className="sidebar-icon" src="/images/album_48dp_5F6368_FILL0_wght400_GRAD0_opsz48.svg" alt="Album Info" />
                      <span className="sidebar-text">앨범 정보 조회</span>
                    </div>
                  </div>
                )}
              </div>
            </div>
          ) : (
            <div className="user-view">
              <div className="profile">
                <img
                  src={`https://soundbrew.storage.s3.ap-northeast-2.amazonaws.com/${user.profileImagePath}`}
                  alt="프로필"
                  className="profile-image"
                />
              </div>
              <div className="info1">
                <span className="myNickname">{user.nickname}</span>
                <span className="plans">회원</span>
              </div>
              <div className="sidebar-my-info" onClick={() => setUserInfoOpen(prev => !prev)}>
                <div className="sidebar-item">
                  <img className="sidebar-icon" src="/images/info_24dp_5F6368_FILL0_wght400_GRAD0_opsz24.svg" alt="내 정보" />
                  <span className="sidebar-text">내 정보</span>
                </div>
                {userInfoOpen && (
                  <div className="user-view-content">
                    <div className="sidebar-item">
                      <img className="sidebar-icon" src="/images/psychology_alt_24dp_5F6368_FILL0_wght400_GRAD0_opsz24.svg" alt="내 정보 조회" />
                      <span className="sidebar-text">내 정보 조회</span>
                    </div>
                    <div className="sidebar-item">
                      <img className="sidebar-icon" src="/images/psychology_alt_24dp_5F6368_FILL0_wght400_GRAD0_opsz24.svg" alt="비밀번호 변경" />
                      <span className="sidebar-text">비밀번호 변경</span>
                    </div>
                  </div>
                )}
              </div>
            </div>
          )}
        </>
      )}

      <hr className="divider" />

      {/* 기타 메뉴들 */}
      <div className="sidebar-item" id="soundTracksRoute">
        <img className="sidebar-icon" src={soundIcon} alt="곡" />
        <span className="sidebar-text">곡</span>
      </div>
      <div className="sidebar-item" id="subscriptionListRoute">
        <img className="sidebar-icon" src={subscriptionIcon} alt="구독제" />
        <span className="sidebar-text">구독제</span>
      </div>
      <hr className="divider" />
      <div className="sidebar-item">
        <img className="sidebar-icon" src={qnaIcon} alt="문의" />
        <span className="sidebar-text">문의</span>
      </div>
      <div className="toggle-container">
        <img src={lightModeIcon} alt="Light Mode" />
        <input type="checkbox" id="darkModeToggle" className="darkMode-checkbox" />
        <label htmlFor="darkModeToggle" className="darkMode-label"></label>
        <img src={darkModeIcon} alt="Dark Mode" />
      </div>
    </div>
  );
};

export default Sidebar;
