// src/App.js
import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import AppRouter from "./routes/AppRouter";
import { setGlobalNavigate } from "./utils/response/globalNavigate";
import { GlobalStateProvider } from "./state/State"; // 전역 상태 관리 (Context API)

// AppWithNavigationSetter는 useNavigate를 사용해 글로벌 네비게이션 함수를 설정하는 컴포넌트입니다.
function AppWithNavigationSetter() {
  const navigate = useNavigate();

  useEffect(() => {
    // 글로벌 네비게이션 함수 설정
    setGlobalNavigate(navigate);
  }, [navigate]);

  return <AppRouter />;
}

function App() {
  return (
    <GlobalStateProvider>
      <AppWithNavigationSetter />
    </GlobalStateProvider>
  );
}

export default App;
